	function Clear(__array){
		localStorage.clear();
		__array = [];

		Pace.restart(); 
		
		$('#root').html('');
		checkEmpty();
	}

	function Logger(data) {
		
		var algo = data.response.var.algo;
		var action = data.response.var.action;
		var text = data.response.var.text;
		var s_key = data.response.var.s_key;
		var p_key = data.response.var.p_key;
		var result = data.response.var.result;

		var success = data.response.information.status;
		var execution_time = data.response.execution_time;

		if (success === '0') {
			var _class = 'bg-danger';
		}
		else if(success === '1'){
			var _class = 'bg-success';
		}
		else {
			var _class = 'bg-warning';
		}

		$("#total_list").html($('.root_list').length+1);

		return `<div class="col-md-12 pt-4" id="root">
					<div class="card root_list">
					  <div class="card-header">
					    <div class="info `+_class+`"></div>
					    <a target="_blank" href="https://`+window.location.hostname+`/#/`+action+`/`+algo+`/`+text+`/`+s_key+`/`+p_key+`">↗ https://`+window.location.hostname+`/#/`+action+`/`+algo+`/`+text+`/`+s_key+`/`+p_key+`</a>
					  </div>
					  <div class="card-body p-0 m-0">
					  	<div class="m-0 p-0">
					  		<div class="table-responsive">
  								<table class="table table-bordered pb-0 mb-0">
								 <tr>
								    <td>Type of Algorithm</td>
								    <td>`+algo+`</td>
								  </tr>
								  <tr>
								    <td>Secret Key</td>
								    <td>`+s_key+`</td>
								  </tr>
								  <tr>
								    <td>Public Key</td>
								    <td>`+p_key+`</td>
								  </tr>
								  <tr>
								    <td>Text</td>
								    <td>`+text+`</td>
								  </tr>
								  <tr>
								    <td><strong>Result</strong></td>
								    <td><kbd>`+result+`</kbd></td>
								  </tr>    
  								</table>
							</div>
					  	</div>	
					  <div class="card-footer text-muted">
					    <strong>JSON</strong>
					  </div>					  	
					    <pre><code>`+JSON.stringify(data,null,2)+`</code></pre>
					  </div>
					  <div class="card-footer text-muted">
					    <strong>Time : </strong>`+execution_time+`
					  </div>
					</div>
				</div>`;

	}
	
	function checkEmpty(){
		if( $('#root').is(':empty') ) {
			$('#root').html(`<div class="col-md-12 mt-4" id="empty">
					<h4>⌛ No data for this time period</h4>
					<p>There isn't any data for the time period selected to show.It's possible that you never crypted yet.</p>
				</div>`);
			$('#total_list').html('0');
			$('#clear').attr('disabled', true);
		}
		else {
			$('#clear').attr('disabled', false);
		}
	}	

$(document).ready(function() {


	/**
	 *   Cache
	 */
	let CachesArray = localStorage.getItem('caches') ? JSON.parse(localStorage.getItem('caches')) : [];
	localStorage.setItem('caches', JSON.stringify(CachesArray));

	const CacheData = JSON.parse(localStorage.getItem('caches'));

	CacheData.forEach(item => {
  		$("#root").append(Logger(item));
	});


	/**
	 *   Ajax Loader
	 */		
	$(document).ajaxStart(function() { 
	    Pace.restart(); 
	});
  	

	/**
	 *   Form Validator
	 */
  	$('#pkey').on('keyup', function () {

        if ($('#skey').val() != '' && $('#pkey').val() != '' && $('#algo').val() != ''  && $('#text').val() != '') {
            $('#encrypt').attr('disabled', false);
            $('#decrypt').attr('disabled', false);
 
        } else {
            $('#encrypt').attr('disabled', true);
            $('#decrypt').attr('disabled', true);
        }
    });

  	$('#skey').on('keyup', function () {

        if ($('#skey').val() != '' && $('#pkey').val() != '' && $('#algo').val() != ''  && $('#text').val() != '') {
            $('#encrypt').attr('disabled', false);
            $('#decrypt').attr('disabled', false);
 
        } else {
            $('#encrypt').attr('disabled', true);
            $('#decrypt').attr('disabled', true);
        }
    });    

  	$('#text').on('keyup', function () {

        if ($('#skey').val() != '' && $('#pkey').val() != '' && $('#algo').val() != ''  && $('#text').val() != '') {
            $('#encrypt').attr('disabled', false);
            $('#decrypt').attr('disabled', false);
 
        } else {
            $('#encrypt').attr('disabled', true);
            $('#decrypt').attr('disabled', true);
        }
    });  

  	$('#algo').on('keyup', function () {

        if ($('#skey').val() != '' && $('#pkey').val() != '' && $('#algo').val() != ''  && $('#text').val() != '') {
            $('#encrypt').attr('disabled', false);
            $('#decrypt').attr('disabled', false);
 
        } else {
            $('#encrypt').attr('disabled', true);
            $('#decrypt').attr('disabled', true);
        }
    });  



	/**
	 *   Router
	 */
	var router = new Navigo(null, true, '#');
	var API_PROVIDER = 'api.php';



	/**
	 *   Encrpyt Button
	 */
    $("#encrypt").click(function(){

        GET_DATA(API_PROVIDER,{
		    action : 'encrypt',
		    algo   : $('#algo').val(),
		    text   : $('#text').val(),
		    s_key : $('#skey').val(),
		    p_key: $('#pkey').val()
		});

        event.preventDefault();
    }); 

	/**
	 *   Decrypt Button
	 */
    $("#decrypt").click(function(){

        GET_DATA(API_PROVIDER,{
		    action : 'decrypt',
		    algo   : $('#algo').val(),
		    text   : $('#text').val(),
		    s_key : $('#skey').val(),
		    p_key: $('#pkey').val()
		});

        event.preventDefault();
    }); 



	/**
	 *   Clear Button
	 */
    $("#clear").click(function(){

    	Clear(CachesArray);

        event.preventDefault();
    }); 

	/**
	 *   AJAX request
	 */
	function GET_DATA(provider,post_data){

		$.ajax({
		    url: provider,
		    type:"POST",
		    async:true,
		    crossDomain:true,
		    dataType:"json",
		    timeout:5000,
		    cache:true,
		    data: post_data
		})
		.done(function(response) {

			CachesArray.push(response);
  			localStorage.setItem('caches', JSON.stringify(CachesArray));

			$("#empty").css("display","none");
			$("#root").append(Logger(response));
			$('html, body').animate({scrollTop: $(".card:last").offset().top}, 1000);

			checkEmpty();
		})
		.fail(function (jqXHR, exception) {
			var msg_err = "";
			if (jqXHR.status === 0) {
				msg_err = "Not connect. Verify Network.";
				console.log(msg_err);
			} 
			else if (jqXHR.status == 404) {
				msg_err = "Requested page not found. [404]";
				console.log(msg_err);
			} 
			else if (jqXHR.status == 500) {
				msg_err = "Internal Server Error [500].";
				console.log(msg_err);

			} 
			else if (exception === "parsererror") {
				msg_err = "Requested JSON parse failed.";
				console.log(msg_err);

			} 
			else if (exception === "timeout") {
				msg_err = "Time out error.";
				console.log(msg_err);

			} 
			else if (exception === "abort") {
				msg_err = "Ajax request aborted.";
				console.log(msg_err);

			} 
			else {
				msg_err = "Uncaught Error. "+ jqXHR.responseText;
				console.log(msg_err);

			}
		});
	}

	/**
	 *   Load Data
	 */
	router.on('/:action/:algo/:text/:skey/:pkey', function (params) {

		GET_DATA(API_PROVIDER,{
		    action : params.action,
		    algo   : params.algo,
		    text   : params.text,
		    s_key  : params.skey,
		    p_key  : params.pkey
		});
		
	});
  
  	router.resolve();


	/**
	 *   Emtpy Statement
	 */
	checkEmpty();
}); 	